// src/pages/Login.js
import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom'; // <-- Link is now correctly imported here
import { useAuth } from '../context/AuthContext';
import { TextField, Button, Box, Typography, Card, CardContent } from '@mui/material';
// 1. IMPORT THE CSS FILE
import './Login.css'; 

const Login = () => {
    // ... state and handleLogin logic ...
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [message, setMessage] = useState('');
    const { login } = useAuth();
    const navigate = useNavigate();

    const handleLogin = async (e) => {
        e.preventDefault();
        setMessage('');
        try {
            const user = await login(username, password);
            // Redirect logic...
            if (user.role === "ROLE_ADMIN") {
                navigate("/admin/dashboard");
            } else if (user.role === "ROLE_DOCTOR") {
                navigate("/doctor/dashboard");
            } else if (user.role === "ROLE_PATIENT") {
                navigate("/patient/dashboard");
            }
        } catch (error) {
            setMessage("Login failed. Check credentials.");
        }
    };

    return (
        // Apply the CSS class defined in Login.css for centering
        <Box className="login-page-container">
            {/* Apply CSS class for card styling/shadows */}
            <Card className="login-card" sx={{ minWidth: 350 }}>
                <CardContent>
                    <Typography variant="h5" component="div" gutterBottom align="center">
                        Hospital Management Login
                    </Typography>
                    {/* Apply CSS class for field spacing */}
                    <Box component="form" onSubmit={handleLogin} className="login-form-fields">
                        <TextField
                            label="Username"
                            variant="outlined"
                            fullWidth
                            value={username}
                            onChange={(e) => setUsername(e.target.value)}
                            required
                        />
                        <TextField
                            label="Password"
                            type="password"
                            variant="outlined"
                            fullWidth
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                        />
                        <Button type="submit" variant="contained" size="large" color="primary" sx={{ mt: 1 }}>
                            Login
                        </Button>
                        {message && (
                            <Typography color="error" align="center" variant="body2" sx={{ mt: 1 }}>
                                {message}
                            </Typography>
                        )}
                        
                        {/* --- NEW NAVIGATION LINKS SECTION --- */}
                        <Box sx={{ display: 'flex', justifyContent: 'space-between', mt: 2 }}>
                            
                            {/* Register Link */}
                            <Typography 
                                variant="body2" 
                                component={Link} 
                                to="/register" 
                                sx={{ textDecoration: 'none', color: 'primary.main', '&:hover': { textDecoration: 'underline' } }}
                            >
                                New User? **Register**
                            </Typography>
                            
                            {/* Forgot Password Link */}
                            <Typography 
                                variant="body2" 
                                component={Link} 
                                to="/forgot-password" 
                                sx={{ textDecoration: 'none', color: 'text.secondary', '&:hover': { textDecoration: 'underline' } }}
                            >
                                Forgot Password?
                            </Typography>
                        </Box>
                    </Box>
                </CardContent>
            </Card>
        </Box>
    );
};

export default Login;
