// src/pages/MyAppointments.js

import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import Layout from '../components/Layout';
import { useAuth } from '../context/AuthContext';
import appointmentService from '../services/appointmentService';
import { 
    Typography, Paper, Table, TableContainer, TableHead, TableRow, TableCell, TableBody, 
    Alert, CircularProgress, Button, Box, Grid 
} from '@mui/material';
import PostAddIcon from '@mui/icons-material/PostAdd';
import RefreshIcon from '@mui/icons-material/Refresh'; 

// ========================================================================
// APPOINTMENT TABLE COMPONENT (Nested in MyAppointments for simplicity)
// ========================================================================
const AppointmentManagementTable = ({ appointments, role, onStatusUpdate }) => {
    
    // Handles the local update and calls the API (simulated)
    const handleStatusUpdate = async (apptId, currentStatus) => {
        const newStatus = currentStatus === 'SCHEDULED' ? 'COMPLETED' : 'SCHEDULED';
        
        try {
            // NOTE: You must implement the updateStatus API call in appointmentService.js 
            // await appointmentService.updateStatus(apptId, newStatus); 
            
            // For now, we simulate success and call the parent update function
            onStatusUpdate(apptId, newStatus); 

        } catch (error) {
            alert(`Failed to update status. Check API.`);
        }
    };
    
    return (
        <TableContainer component={Paper} sx={{ mt: 2 }}>
            <Table size="small">
                <TableHead>
                    <TableRow sx={{ backgroundColor: '#e0e0e0' }}>
                        <TableCell>Time</TableCell>
                        <TableCell>Doctor</TableCell>
                        <TableCell>Patient</TableCell>
                        <TableCell>Status</TableCell>
                        {role === 'ROLE_DOCTOR' && <TableCell align="center">Manage</TableCell>} 
                        {role === 'ROLE_DOCTOR' && <TableCell align="center">Record</TableCell>} 
                        {/* THE ERROR IS LIKELY HERE OR IN THE PREVIOUS STEP'S HEADER */}
                        
                        {/*
                        // If you found extra headers in the code, remove them now:
                        <TableCell>Record Updated</TableCell> 
                        */}
                    </TableRow>
                </TableHead>
                <TableBody>
                    {appointments.map((appt) => (
                        <TableRow key={appt.id}>
                            <TableCell>{new Date(appt.appointmentTime).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}</TableCell>
                            <TableCell>{appt.doctorName}</TableCell>
                            <TableCell>{appt.patientName}</TableCell>
                            <TableCell>
                                <Typography variant="body2" color={appt.status === 'SCHEDULED' ? 'primary' : appt.status === 'COMPLETED' ? 'success' : 'error'}>
                                    {appt.status}
                                </Typography>
                            </TableCell>
                            
                            {/* 1. Manage Column (Status Update) - DOCTOR ONLY */}
                            {role === 'ROLE_DOCTOR' && (
                                <TableCell align="center">
                                    {appt.status === 'SCHEDULED' ? ( 
                                        <Button 
                                            // ... (Button props) ...
                                            onClick={() => handleStatusUpdate(appt.id, appt.status)}
                                        >
                                            Update Status
                                        </Button>
                                    ) : (
                                        // FIX: Render a dash or empty string when COMPLETED/CANCELED
                                        <Typography variant="caption" color="text.secondary">
                                            -
                                        </Typography>
                                    )}
                                </TableCell>
                            )}

                            {/* 2. Record Column (Creation/Updated Text) - DOCTOR ONLY */}
                           {role === 'ROLE_DOCTOR' && (
                                <TableCell align="center">
                                    {/* --- CRITICAL LOGIC --- */}
                                    {appt.hasRecord ? ( 
                                        // Case 1: Record exists (FINAL PERSISTENT STATE)
                                        <Typography variant="caption" color="success" sx={{ fontWeight: 'bold' }}>
                                            Record Updated
                                        </Typography>
                                    ) : (
                                        // Case 2: Record does NOT exist. Show either the button or a dash based on status.
                                        appt.status === 'COMPLETED' ? (
                                            // Actionable state: Status is COMPLETED, but documentation is missing
                                            <Button 
                                                variant="contained" 
                                                size="small" 
                                                color="success"
                                                startIcon={<PostAddIcon />}
                                                component={Link}
                                                to={`/records/create/${appt.patientId}/${appt.doctorId}`} 
                                                state={{ patientName: appt.patientName, doctorName: appt.doctorName }}
                                            >
                                                Create Record
                                            </Button>
                                        ) : (
                                            // Case 3: Status is SCHEDULED (Nothing to do yet)
                                            <Typography variant="caption" color="text.secondary">
                                                -
                                            </Typography>
                                        )
                                    )}
                                </TableCell>
                            )}
                            
                            {/* 2. Record Column (Creation/Updated Text) - DOCTOR ONLY */}
                            {/* CRITICAL: Use the hasRecord flag */}
                            {role === 'ROLE_DOCTOR' && (
                                <TableCell align="center">
                                    {appt.status === 'COMPLETED' ? ( 
                                        appt.hasRecord ? ( 
                                            // 1. FINAL STATE: Record exists
                                            <Typography variant="caption" color="success" sx={{ fontWeight: 'bold' }}>
                                                Record Updated
                                            </Typography>
                                        ) : (
                                            // 2. ACTIONABLE STATE: Needs documentation
                                            <Button 
                                                variant="contained" 
                                                size="small" 
                                                color="success"
                                                startIcon={<PostAddIcon />}
                                                component={Link}
                                                to={`/records/create/${appt.patientId}/${appt.doctorId}`} 
                                                state={{ patientName: appt.patientName, doctorName: appt.doctorName }}
                                            >
                                                Create Record
                                            </Button>
                                        )
                                    ) : (
                                        // Status is SCHEDULED (Nothing to do yet)
                                        <Typography variant="caption" color="text.secondary">
                                            -
                                        </Typography>
                                    )}
                                </TableCell>
                            )}
                        </TableRow>
                    ))}
                </TableBody>
            </Table>
        </TableContainer>
    );
};
// ========================================================================


// ========================================================================
// MAIN COMPONENT: MyAppointments
// ========================================================================
const MyAppointments = () => {
    const { role } = useAuth();
    const [appointments, setAppointments] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    // Fetch appointments when the component mounts
    const fetchAppointments = async () => {
        try {
            const data = await appointmentService.getMyAppointments();
            setAppointments(data);
        } catch (err) {
            setError("Failed to fetch appointments.");
            console.error("Appointment fetch error:", err);
        } finally {
            setLoading(false);
        }
    };

    // Load appointments on component mount
    useEffect(() => {
        fetchAppointments();
    }, []);

    // Function to update local state after a successful API status change
    const handleLocalStatusUpdate = (apptId, newStatus) => {
        // Find the appointment and update its status locally to refresh the UI immediately
        setAppointments(prev => 
            prev.map(a => a.id === apptId ? { ...a, status: newStatus } : a)
        );
    };


    if (loading) return <Layout><Box sx={{ display: 'flex', justifyContent: 'center', mt: 4 }}><CircularProgress /></Box></Layout>;
    
    return (
        <Layout>
            <Box sx={{ p: 3 }}>
                <Typography variant="h4" gutterBottom>
                    My Appointments
                </Typography>

                {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}
                
                {/* Render the full management table */}
                <AppointmentManagementTable 
                    appointments={appointments} 
                    role={role}
                    onStatusUpdate={handleLocalStatusUpdate}
                />
            </Box>
        </Layout>
    );
};

export default MyAppointments;
