// src/pages/Register.js

import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { TextField, Button, Box, Typography, Card, CardContent, FormControl, InputLabel, Select, MenuItem, CircularProgress } from '@mui/material';
import axios from 'axios';
import './Login.css'; // Reuse login styling

const Register = () => {
    // --- State Management ---
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [fullName, setFullName] = useState('');         // NEW: Full Name state
    const [mobileNumber, setMobileNumber] = useState(''); // NEW: Mobile Number state
    const [specialization, setSpecialization] = useState(''); // NEW: Specialization state
    const [role, setRole] = useState('ROLE_PATIENT'); 
    const [message, setMessage] = useState('');
    const [submitting, setSubmitting] = useState(false);
    
    const navigate = useNavigate();

    const handleRegister = async (e) => {
        e.preventDefault();
        setMessage('');
        setSubmitting(true);
        
        // 1. Construct the Payload
        const payload = { 
            username, 
            password,
            fullName, 
            mobileNumber,
            role,
            // CRITICAL: Only include specialization if registering as a Doctor
            specialization: role === 'ROLE_DOCTOR' ? specialization : null 
        };
        
        try {
            // 2. Submit to Backend
            await axios.post("/api/auth/register", payload);
            
            setMessage("Registration successful! You can now log in.");
            
            // 3. Redirect to login after a short delay
            setTimeout(() => navigate('/'), 2000); 
        } catch (error) {
            console.error("Registration Error:", error);
            const errorMsg = error.response?.data || "Server error. Check inputs.";
            setMessage("Registration failed. " + errorMsg);
        } finally {
            setSubmitting(false);
        }
    };

    return (
        <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', minHeight: '100vh' }}>
            <Card className="login-card" sx={{ minWidth: 350, maxWidth: 400, p: 3, boxShadow: 3 }}>
                <CardContent>
                    <Typography variant="h5" gutterBottom align="center">
                        User Registration
                    </Typography>
                    
                    {/* Form container with spacing */}
                    <Box component="form" onSubmit={handleRegister} sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
                        
                        {/* Full Name */}
                        <TextField 
                            label="Full Name" 
                            fullWidth 
                            required 
                            value={fullName} 
                            onChange={(e) => setFullName(e.target.value)}
                        />
                        
                        {/* Mobile Number */}
                        <TextField 
                            label="Mobile Number" 
                            fullWidth 
                            required 
                            value={mobileNumber} 
                            onChange={(e) => setMobileNumber(e.target.value)}
                        />

                        {/* Username */}
                        <TextField 
                            label="Username" 
                            fullWidth 
                            required 
                            value={username} 
                            onChange={(e) => setUsername(e.target.value)}
                        />
                        
                        {/* Password */}
                        <TextField 
                            label="Password" 
                            type="password" 
                            fullWidth 
                            required 
                            value={password} 
                            onChange={(e) => setPassword(e.target.value)}
                        />
                        
                        {/* Role Selection */}
                        <FormControl fullWidth variant="outlined" sx={{ mt: 1 }}>
                            <InputLabel id="role-label">Register As</InputLabel>
                            <Select 
                                labelId="role-label" 
                                id="role-select" 
                                value={role} 
                                label="Register As" 
                                onChange={(e) => {
                                    setRole(e.target.value);
                                    setSpecialization(''); // Clear specialization when switching roles
                                }}
                            >
                                <MenuItem value={"ROLE_PATIENT"}>Patient</MenuItem>
                                <MenuItem value={"ROLE_DOCTOR"}>Doctor</MenuItem>
                            </Select>
                        </FormControl>
                        
                        {/* Conditional: Specialization Field for Doctors */}
                        {role === 'ROLE_DOCTOR' && (
                            <TextField 
                                label="Specialization (e.g., Cardiology)" 
                                fullWidth 
                                required 
                                value={specialization} 
                                onChange={(e) => setSpecialization(e.target.value)}
                            />
                        )}

                        {/* Submission Button */}
                        <Button 
                            type="submit" 
                            variant="contained" 
                            size="large" 
                            sx={{ mt: 2 }}
                            disabled={submitting}
                        >
                            {submitting ? <CircularProgress size={24} color="inherit" /> : 'Register Account'}
                        </Button>

                        {/* Messages and Login Link */}
                        {message && (<Typography color={message.includes("successful") ? 'success' : 'error'} align="center" sx={{ mt: 1 }}>{message}</Typography>)}
                        
                        <Typography variant="body2" component={Link} to="/" align="center" sx={{ mt: 2 }}>
                            Already have an account? Login
                        </Typography>
                    </Box>
                </CardContent>
            </Card>
        </Box>
    );
};

export default Register;
