// src/pages/BookAppointment.js

import React, { useState, useEffect } from 'react';
import Layout from '../components/Layout';
import { 
    Typography, 
    Box, 
    TextField, 
    Button, 
    CircularProgress, 
    Alert, 
    FormControl, 
    InputLabel, 
    Select, 
    MenuItem,
    Grid,
    Card,           
    CardContent     
} from '@mui/material';
import appointmentService from '../services/appointmentService';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';

const BookAppointment = () => {
    const { user, role } = useAuth();
    const navigate = useNavigate();
    
    // State for form data, doctor list, and UI feedback
    const [doctors, setDoctors] = useState([]);
    const [formData, setFormData] = useState({
        doctorId: '', // Initialized as empty string for Select placeholder
        patientId: user.role === 'ROLE_PATIENT' ? user.id : '', 
        appointmentTime: '',
        reason: '',
    });
    const [loading, setLoading] = useState(true);
    const [submitting, setSubmitting] = useState(false);
    const [message, setMessage] = useState(null);

    // Fetch list of doctors on component mount
    useEffect(() => {
        const fetchDoctors = async () => {
            try {
                const docList = await appointmentService.getDoctors();
                setDoctors(docList);
            } catch (err) {
                setMessage({ type: 'error', text: 'Failed to load doctor list. Check backend connectivity.' });
            } finally {
                setLoading(false);
            }
        };
        fetchDoctors();
    }, []);

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setSubmitting(true);
        setMessage(null);
        
        try {
            const appointmentRequest = {
                ...formData,
                appointmentTime: new Date(formData.appointmentTime).toISOString().slice(0, 19), 
            };

            await appointmentService.bookAppointment(appointmentRequest);
            setMessage({ type: 'success', text: 'Appointment booked successfully! Redirecting...' });
            setTimeout(() => navigate('/appointments/list'), 2000);

        } catch (error) {
            console.error("Booking Error:", error);
            setMessage({ type: 'error', text: error.response?.data.message || 'Failed to book appointment.' });
        } finally {
            setSubmitting(false);
        }
    };

    if (loading) return <Layout><CircularProgress sx={{ display: 'block', margin: '20px auto' }} /></Layout>;


    return (
        <Layout>
            <Typography variant="h4" gutterBottom>
                Book New Appointment
            </Typography>
            
            {/* ... Message Alert ... */}

            <Box component="form" onSubmit={handleSubmit} sx={{ mt: 3, mx: 'auto', maxWidth: 850 }}>
                {/* Fixed Grid Spacing */}
                <Grid container spacing={4}> 
                    
                    {/* COLUMN 1: Doctor Selection & Patient ID (Takes half width on MD screens) */}
                    {/* Changed xs={24} to xs={12} and md={6} for reliable columns */}
                    <Grid item xs={12} md={6}>
                        
                        {/* 1. Doctor Selection */}
                        <FormControl fullWidth required variant="outlined" sx={{ mb: 3 }}>
                            
                            <Select
                                labelId="doctor-label"
                                name="doctorId"
                                value={formData.doctorId}
                                label="Select Doctor"
                                fullWidth // This prop is on the Select, but fullWidth on FormControl is primary
                                onChange={handleChange}
                            >
                                <MenuItem value="">
                                    <em>None (Please Select)</em>
                                </MenuItem>
                                {doctors.map((doc) => (
                                    <MenuItem key={doc.id} value={doc.id}>
                                        {doc.name} - {doc.specialization}
                                    </MenuItem>
                                ))}
                            </Select>
                        </FormControl>

                        {/* Patient ID/Name Input (Conditional) */}
                        {role !== 'ROLE_PATIENT' && (
                            <TextField
                                label="Patient ID (Who is receiving treatment)"
                                name="patientId"
                                type="number"
                                fullWidth
                                required
                                value={formData.patientId}
                                onChange={handleChange}
                                variant="outlined"
                            />
                        )}
                    </Grid>

                    {/* COLUMN 2: Date/Time & Reason (Takes half width on MD screens) */}
                    <Grid item xs={12} md={6}>
                        {/* 3. Date/Time Picker */}
                        <TextField
                            label="Appointment Date and Time"
                            name="appointmentTime"
                            type="datetime-local"
                            fullWidth
                            required
                            InputLabelProps={{ shrink: true }}
                            value={formData.appointmentTime}
                            onChange={handleChange}
                            variant="outlined"
                            sx={{ mb: 3 }}
                        />
                        
                        {/* 4. Reason */}
                        <TextField
                            label="Reason for Appointment"
                            name="reason"
                            multiline
                            rows={5} 
                            fullWidth
                            value={formData.reason}
                            onChange={handleChange}
                            variant="outlined"
                        />
                    </Grid>

                    {/* Submit Button (Spans entire width) */}
                    <Grid item xs={12}>
                        <Button 
                            type="submit" 
                            variant="contained" 
                            size="large"
                            color="primary" 
                            fullWidth 
                            disabled={submitting}
                        >
                            {submitting ? <CircularProgress size={24} color="inherit" /> : 'Confirm Booking'}
                        </Button>
                    </Grid>
                </Grid>
            </Box>
        </Layout>
    );
};


export default BookAppointment;
