// src/pages/BookAppointment.js

import React, { useState, useEffect } from 'react';
import Layout from '../components/Layout';
import { 
    Typography, 
    Box, 
    TextField, 
    Button, 
    CircularProgress, 
    Alert, 
    FormControl, 
    InputLabel, 
    Select, 
    MenuItem,
    Grid // CRITICAL: Ensure Grid is imported
} from '@mui/material';
import appointmentService from '../services/appointmentService';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';

const BookAppointment = () => {
    const { user, role } = useAuth();
    const navigate = useNavigate();
    
    // State for form data, doctor list, and UI feedback
    const [doctors, setDoctors] = useState([]);
    const [formData, setFormData] = useState({
        doctorId: '',
        // Assuming user.id is the base User ID needed for the patientId field
        patientId: user.role === 'ROLE_PATIENT' ? user.id : '', 
        appointmentTime: '',
        reason: '',
    });
    const [loading, setLoading] = useState(true);
    const [submitting, setSubmitting] = useState(false);
    const [message, setMessage] = useState(null);

    // Fetch list of doctors on component mount
    useEffect(() => {
        const fetchDoctors = async () => {
            try {
                // IMPORTANT: The getDoctors function needs to be correctly implemented in the backend (GET /api/doctors)
                const docList = await appointmentService.getDoctors();
                setDoctors(docList);
            } catch (err) {
                setMessage({ type: 'error', text: 'Failed to load doctor list. Check backend.' });
            } finally {
                setLoading(false);
            }
        };
        // The service needs to exist and be functional
        if (appointmentService.getDoctors) {
             fetchDoctors();
        } else {
             // Fallback if service function is missing
             setLoading(false);
             setMessage({ type: 'error', text: 'Appointment service methods not fully defined.' });
        }
    }, []);

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setSubmitting(true);
        setMessage(null);
        
        try {
            const appointmentRequest = {
                ...formData,
                appointmentTime: new Date(formData.appointmentTime).toISOString().slice(0, 19), 
            };

            await appointmentService.bookAppointment(appointmentRequest);
            setMessage({ type: 'success', text: 'Appointment booked successfully! Redirecting...' });
            setTimeout(() => navigate('/appointments/list'), 2000);

        } catch (error) {
            console.error("Booking Error:", error);
            setMessage({ type: 'error', text: error.response?.data.message || 'Failed to book appointment.' });
        } finally {
            setSubmitting(false);
        }
    };

    if (loading) return <Layout><CircularProgress /></Layout>;

    return (
        <Layout>
            {/* The outer centering wrapper */}
            <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center', width: '100%' }}>
                
                <Typography variant="h4" gutterBottom align="center" sx={{ mb: 4 }}>
                    Book New Appointment
                </Typography>
                
                {message && (
                    <Alert severity={message.type} sx={{ mb: 3, maxWidth: 450, width: '100%' }}>
                        {message.text}
                    </Alert>
                )}

                {/* The form container with constrained width */}
                <Box component="form" onSubmit={handleSubmit} sx={{ width: '100%', maxWidth: 450 }}>
                    
                    {/* Grid system ensures clean vertical stacking with spacing */}
                    <Grid container spacing={3}> 
                        
                        {/* 1. Doctor Selection */}
                        <Grid item xs={12}>
                            <FormControl fullWidth required variant="outlined">
                                <InputLabel id="doctor-label">Select Doctor</InputLabel>
                                <Select
                                    labelId="doctor-label"
                                    name="doctorId"
                                    value={formData.doctorId}
                                    label="Select Doctor"
                                    onChange={handleChange}
                                >
                                    <MenuItem value="">
                                        <em>None (Please Select)</em>
                                    </MenuItem>
                                    {doctors.map((doc) => (
                                        <MenuItem key={doc.id} value={doc.id}>
                                            {doc.name} - {doc.specialization}
                                        </MenuItem>
                                    ))}
                                </Select>
                            </FormControl>
                        </Grid>

                        {/* 2. Patient ID Input (Conditional) */}
                        {user.role !== 'ROLE_PATIENT' && (
                            <Grid item xs={12}>
                                <TextField
                                    label="Patient ID (Who is receiving treatment)"
                                    name="patientId"
                                    type="number"
                                    fullWidth
                                    required
                                    value={formData.patientId}
                                    onChange={handleChange}
                                    variant="outlined"
                                />
                            </Grid>
                        )}

                        {/* 3. Date/Time Picker */}
                        <Grid item xs={12}>
                            <TextField
                                label="Appointment Date and Time"
                                name="appointmentTime"
                                type="datetime-local"
                                fullWidth
                                required
                                InputLabelProps={{ shrink: true }}
                                value={formData.appointmentTime}
                                onChange={handleChange}
                                variant="outlined"
                            />
                        </Grid>
                        
                        {/* 4. Reason */}
                        <Grid item xs={12}>
                            <TextField
                                label="Reason for Appointment"
                                name="reason"
                                multiline
                                rows={4} 
                                fullWidth
                                value={formData.reason}
                                onChange={handleChange}
                                variant="outlined"
                            />
                        </Grid>

                        {/* 5. Submit Button */}
                        <Grid item xs={12}>
                            <Button 
                                type="submit" 
                                variant="contained" 
                                size="large"
                                color="primary" 
                                fullWidth 
                                disabled={submitting}
                            >
                                {submitting ? <CircularProgress size={24} color="inherit" /> : 'Confirm Booking'}
                            </Button>
                        </Grid>
                    </Grid>
                </Box>
            </Box>
        </Layout>
    );
};

export default BookAppointment;
