// src/pages/MyRecords.js

import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom'; // <-- NEW: Import Link for navigation
import Layout from '../components/Layout';
import { 
    Typography, Paper, Table, TableContainer, TableHead, TableRow, TableCell, TableBody, 
    Alert, CircularProgress, Box, Button // <-- Ensure Button is imported
} from '@mui/material';
import VisibilityIcon from '@mui/icons-material/Visibility'; // <-- NEW: Import icon
import medicalRecordService from '../services/medicalRecordService';
import { useAuth } from '../context/AuthContext';

const MyRecords = () => {
    const { role } = useAuth();
    const [records, setRecords] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    // ... (useEffect and data fetching logic remains unchanged) ...

    useEffect(() => {
        const fetchRecords = async () => {
            try {
                const data = await medicalRecordService.getMyAccessibleRecords();
                setRecords(data);
            } catch (err) {
                setError("Failed to fetch medical records. Access denied or server error.");
                console.error("Records fetch error:", err);
            } finally {
                setLoading(false);
            }
        };
        fetchRecords();
    }, []);

    if (loading) return <Layout><Box sx={{ display: 'flex', justifyContent: 'center', mt: 4 }}><CircularProgress /></Box></Layout>;
    
    /**
     * Helper to define table columns and order based on the user's role.
     */
    const getColumns = () => {
        let base = [
            { id: 'recordedAt', label: 'Date' },
            { id: 'diagnosis', label: 'Diagnosis' },
        ];

        // Insert Doctor/Patient Names in a specific order:
        if (role === 'ROLE_ADMIN') {
            base.push({ id: 'patientName', label: 'Patient' });
            base.push({ id: 'doctorName', label: 'Doctor' });
        } else if (role === 'ROLE_DOCTOR') {
            base.push({ id: 'patientName', label: 'Patient' }); // Doctor needs to know which patient
        } else if (role === 'ROLE_PATIENT') {
            base.push({ id: 'doctorName', label: 'Doctor' });   // Patient needs to know which doctor
        }
        
        base.push({ id: 'consultationNotes', label: 'Notes Preview' });
        
        // Add Action column for anyone allowed to view/edit (Doctors and Admins)
        if (role === 'ROLE_DOCTOR' || role === 'ROLE_ADMIN') {
            base.push({ id: 'action', label: 'Action', align: 'center' });
        }
        
        return base;
    };

    const columns = getColumns();

    return (
        <Layout>
            <Typography variant="h4" gutterBottom>
                {role === 'ROLE_PATIENT' ? 'My Medical History' : 'Accessible Medical Records'}
            </Typography>

            {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}
            
            {records.length === 0 ? (
                <Alert severity="info">No medical records found for your profile.</Alert>
            ) : (
                <TableContainer component={Paper}>
                    <Table>
                        <TableHead>
                            <TableRow sx={{ backgroundColor: '#f5f5f5' }}>
                                {columns.map((col) => (
                                    // Use align prop from the column definition
                                    <TableCell key={col.id} align={col.align || 'left'}>{col.label}</TableCell>
                                ))}
                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {records.map((record) => (
                                <TableRow key={record.id}>
                                    <TableCell>{new Date(record.recordedAt).toLocaleDateString()}</TableCell>
                                    <TableCell>{record.diagnosis}</TableCell>
                                    
                                    {/* Conditional Column RENDERING (must match order in getColumns) */}
                                    {/* Doctor/Admin columns */}
                                    {(role === 'ROLE_DOCTOR' || role === 'ROLE_ADMIN') && <TableCell>{record.patientName}</TableCell>}
                                    {/* Patient/Admin columns */}
                                    {(role === 'ROLE_PATIENT' || role === 'ROLE_ADMIN') && <TableCell>{record.doctorName}</TableCell>}
                                    
                                    <TableCell>{record.consultationNotes.substring(0, 50)}...</TableCell>
                                    
                                    {/* NEW: Action Cell (Visible to Doctor/Admin) */}
                                    {(role === 'ROLE_DOCTOR' || role === 'ROLE_ADMIN') && (
                                        <TableCell align="center">
                                            <Button 
                                                variant="outlined" 
                                                size="small" 
                                                color="info"
                                                startIcon={<VisibilityIcon />}
                                                component={Link}
                                                // Link to the View/Edit route, passing the unique record ID
                                                to={`/records/view/${record.id}`} 
                                            >
                                                View/Edit
                                            </Button>
                                        </TableCell>
                                    )}
                                </TableRow>
                            ))}
                        </TableBody>
                    </Table>
                </TableContainer>
            )}
        </Layout>
    );
};

export default MyRecords;
