// src/pages/DoctorDashboard.js

import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Layout from '../components/Layout';
import { 
    Typography, Grid, Card, CardContent, CardActionArea, Box, 
    Alert, CircularProgress, Button, Paper 
    // Ensure all necessary components for the table are imported
} from '@mui/material';
import EventAvailableIcon from '@mui/icons-material/EventAvailable';
import PeopleIcon from '@mui/icons-material/People';
import PostAddIcon from '@mui/icons-material/PostAdd'; 
import RefreshIcon from '@mui/icons-material/Refresh'; 
import appointmentService from '../services/appointmentService';
import { useAuth } from '../context/AuthContext'; 

// NOTE: DoctorAppointmentTable component definition must remain *above* DoctorDashboard.

const DoctorDashboard = () => {
    const { user, role } = useAuth();
    const [appointments, setAppointments] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    // ... (fetchAppointments, useEffect, and handleLocalStatusUpdate logic remains unchanged) ...
    // Note: The DoctorAppointmentTable component definition is assumed to be placed outside this function.

    const doctorModules = [
        { 
            title: "Today's Schedule", 
            description: "View upcoming and recent appointments.", 
            icon: <EventAvailableIcon sx={{ fontSize: 40 }} color="primary" />, 
            path: "/appointments/list" // Links to the complete list view (if needed)
        },
        { 
            title: "Patient Records", 
            description: "Access and document patient medical history.", 
            icon: <PeopleIcon sx={{ fontSize: 40 }} color="success" />, 
            path: "/records/list" 
        },
    ];

    if (loading) return <Layout><Box sx={{ display: 'flex', justifyContent: 'center', p: 3 }}><CircularProgress /></Box></Layout>;

    return (
        <Layout>
            <Box sx={{ p: 4 }}>
                
                {/* 1. Welcome Hero Section (Styled like Patient Dashboard) */}
                <Box sx={{ 
                    mb: 5, 
                    p: 4, 
                    backgroundColor: '#e3f2fd', // Light blue background
                    borderRadius: 2,
                    boxShadow: 2
                }}>
                    <Typography variant="h3" gutterBottom color="primary.dark">
                        Welcome back, Dr. {user.username}!
                    </Typography>
                    <Typography variant="h6" color="text.secondary">
                        Your management hub for patient care and documentation.
                    </Typography>
                </Box>
                
                {/* 2. Quick Action Modules */}
                <Typography variant="h5" gutterBottom sx={{ mb: 3 }}>
                    Quick Actions
                </Typography>
                
                <Grid container spacing={4}>
                    {doctorModules.map((module) => (
                        <Grid item xs={12} sm={6} md={4} key={module.title}>
                            <Card sx={{ height: '100%', borderLeft: '5px solid #1a76d2' }}>
                                <CardActionArea component={Link} to={module.path}>
                                    <CardContent>
                                        <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                                            {module.icon}
                                            <Typography variant="h6" component="div" sx={{ ml: 2 }}>
                                                {module.title}
                                            </Typography>
                                        </Box>
                                        <Typography variant="body2" color="text.secondary">
                                            {module.description}
                                        </Typography>
                                    </CardContent>
                                </CardActionArea>
                            </Card>
                        </Grid>
                    ))}
                </Grid>

                {/* 3. Schedule List (The actual table content from previous steps) */}
                <Typography variant="h5" gutterBottom sx={{ mt: 5 }}>
                    Today's Schedule
                </Typography>
                
                {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}
                
                {/* Renders the Appointment Table OR the "No appointments" alert */}
                <DoctorAppointmentTable 
                    appointments={appointments} 
                    role={role}
                    onStatusUpdate={handleLocalStatusUpdate} 
                />
                
            </Box>
        </Layout>
    );
};

export default DoctorDashboard;
