// src/pages/MyAppointments.js

import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom'; // <-- NEW IMPORT: For navigation button
import Layout from '../components/Layout';
import { useAuth } from '../context/AuthContext'; // <-- NEW IMPORT: To get the user role
import appointmentService from '../services/appointmentService'; 
import { 
    Typography, 
    Paper, 
    Table, 
    TableContainer, 
    TableHead, 
    TableRow, 
    TableCell, 
    TableBody, 
    Alert, 
    CircularProgress, 
    Button, // <-- Ensure Button is imported
    Box // Ensure Box is imported for the main layout/loading state
} from '@mui/material';
import PostAddIcon from '@mui/icons-material/PostAdd'; // Icon for new record

const MyAppointments = () => {
    const { user, role } = useAuth(); // Get the role to apply conditional logic
    const [appointments, setAppointments] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchAppointments = async () => {
            try {
                const data = await appointmentService.getMyAppointments();
                setAppointments(data);
            } catch (err) {
                setError("Failed to fetch appointments. Check backend connectivity.");
                console.error("Appointment fetch error:", err);
            } finally {
                setLoading(false);
            }
        };
        fetchAppointments();
    }, []);

    if (loading) return <Layout><Box sx={{ display: 'flex', justifyContent: 'center', mt: 4 }}><CircularProgress /></Box></Layout>;
    
    return (
        <Layout>
            <Typography variant="h4" gutterBottom>
                My Appointments
            </Typography>

            {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}
            
            {appointments.length === 0 ? (
                <Alert severity="info">You have no scheduled appointments.</Alert>
            ) : (
                <TableContainer component={Paper}>
                    <Table>
                        <TableHead>
                            <TableRow sx={{ backgroundColor: '#f5f5f5' }}>
                                <TableCell>Date & Time</TableCell>
                                <TableCell>Doctor</TableCell>
                                <TableCell>Patient</TableCell>
                                <TableCell>Reason</TableCell>
                                <TableCell>Status</TableCell>
                                {/* NEW: Action column visible only to Doctors */}
                                {role === 'ROLE_DOCTOR' && <TableCell align="center">Record</TableCell>} 
                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {appointments.map((appt) => (
                                <TableRow key={appt.id}>
                                    <TableCell>{new Date(appt.appointmentTime).toLocaleString()}</TableCell>
                                    <TableCell>{appt.doctorName}</TableCell>
                                    <TableCell>{appt.patientName}</TableCell>
                                    <TableCell>{appt.reason}</TableCell>
                                    <TableCell>
                                        <Typography 
                                            variant="body2" 
                                            color={appt.status === 'SCHEDULED' ? 'primary' : appt.status === 'COMPLETED' ? 'success' : 'error'}
                                        >
                                            {appt.status}
                                        </Typography>
                                    </TableCell>
                                    
                                    {/* NEW: Action Button for Doctors */}
                                    {role === 'ROLE_DOCTOR' && (
                                        <TableCell align="center">
                                            {appt.status === 'COMPLETED' ? ( 
                                                <Button 
                                                    variant="contained" 
                                                    size="small" 
                                                    color="success"
                                                    startIcon={<PostAddIcon />}
                                                    component={Link} // Use Link for navigation
                                                    // Pass data via URL params and state for the record creation page
                                                    to={`/records/create/${appt.patientId}/${appt.doctorId}`} 
                                                    state={{ 
                                                        patientName: appt.patientName, 
                                                        doctorName: appt.doctorName, 
                                                        apptId: appt.id 
                                                    }}
                                                >
                                                    Create Record
                                                </Button>
                                            ) : (
                                                <Typography variant="caption" color="text.secondary">
                                                    (Must be COMPLETED)
                                                </Typography>
                                            )}
                                        </TableCell>
                                    )}
                                </TableRow>
                            ))}
                        </TableBody>
                    </Table>
                </TableContainer>
            )}
        </Layout>
    );
};

export default MyAppointments;
