// src/pages/BookAppointment.js
import React, { useState, useEffect } from 'react';
import Layout from '../components/Layout';
import { Typography, Box, TextField, Button, CircularProgress, Alert, FormControl, InputLabel, Select, MenuItem,Grid } from '@mui/material';
import appointmentService from '../services/appointmentService';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';

const BookAppointment = () => {
    const { user, role } = useAuth();
    const navigate = useNavigate();
    
    // State for form data, doctor list, and UI feedback
    const [doctors, setDoctors] = useState([]);
    const [formData, setFormData] = useState({
        doctorId: '',
        patientId: user.role === 'ROLE_PATIENT' ? user.id : '', // Pre-fill patientId if current user is a patient
        appointmentTime: '',
        reason: '',
    });
    const [loading, setLoading] = useState(true);
    const [submitting, setSubmitting] = useState(false);
    const [message, setMessage] = useState(null);

    // Fetch list of doctors on component mount
    useEffect(() => {
        const fetchDoctors = async () => {
            try {
                const docList = await appointmentService.getDoctors();
                setDoctors(docList);
                setLoading(false);
            } catch (err) {
                setMessage({ type: 'error', text: 'Failed to load doctor list. Check backend connectivity.' });
                setLoading(false);
            }
        };
        fetchDoctors();
    }, []);

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setSubmitting(true);
        setMessage(null);

        // NOTE: The backend requires the base User ID, not the Patient/Doctor profile ID, for the appointment request.
        // We will pass the base User ID for the patient/doctor. 
        // IMPORTANT: The backend service must look up the correct Patient/Doctor profile ID from this base User ID.
        
        try {
            // Only patientId/doctorId are required for the request (backend handles conversion)
            const appointmentRequest = {
                ...formData,
                // Ensure date format is ISO string (LocalDateTime format for Spring Boot)
                appointmentTime: new Date(formData.appointmentTime).toISOString().slice(0, 19), 
                // Currently sending user.id as patientId, assuming backend will validate/lookup Patient profile
                patientId: formData.patientId, 
            };

            await appointmentService.bookAppointment(appointmentRequest);
            setMessage({ type: 'success', text: 'Appointment booked successfully!' });
            setTimeout(() => navigate('/appointments/list'), 2000);

        } catch (error) {
            console.error("Booking Error:", error);
            setMessage({ type: 'error', text: error.response?.data || 'Failed to book appointment. Please check inputs.' });
        } finally {
            setSubmitting(false);
        }
    };

    if (loading) return <Layout><CircularProgress /></Layout>;

    return (
    <Layout>
        <Typography variant="h4" gutterBottom>
            Book New Appointment
        </Typography>
        
        {/* ... Message Alert ... */}

        <Box component="form" onSubmit={handleSubmit} sx={{ mt: 3 }}>
            <Grid container spacing={4} sx={{ maxWidth: 800 }}> 
                
                {/* COLUMN 1: Doctor Selection & Patient ID */}
                <Grid item xs={12} md={6}>
                    
                    {/* --- REVISED DOCTOR SELECTION FIELD --- */}
                    <FormControl fullWidth required variant="outlined" sx={{ mb: 3 }}>
                        <InputLabel id="doctor-label">Select Doctor</InputLabel>
                        <Select
                            labelId="doctor-label"
                            name="doctorId"
                            value={formData.doctorId}
                            label="Select Doctor" // <-- Crucial: Matches the InputLabel text
                            onChange={handleChange}
                        >
                            {/* The empty placeholder item */}
                            <MenuItem value="">
                                <em>None (Please Select)</em>
                            </MenuItem>
                            
                            {/* Mapping through the fetched doctors */}
                            {doctors.map((doc) => (
                                <MenuItem key={doc.id} value={doc.id}>
                                    {doc.name} - {doc.specialization}
                                </MenuItem>
                            ))}
                        </Select>
                    </FormControl>

                    {/* Patient ID/Name Input (Only visible if not a patient booking for self) */}
                    {role !== 'ROLE_PATIENT' && (
                        <TextField
                            label="Patient ID (Who is receiving treatment)"
                            name="patientId"
                            type="number"
                            fullWidth
                            required
                            value={formData.patientId}
                            onChange={handleChange}
                            sx={{ mb: 3 }} // Added bottom margin for spacing
                        />
                    )}
                </Grid>

                {/* COLUMN 2: Date/Time & Reason (unchanged) */}
                <Grid item xs={12} md={6}>
                    <TextField
                        label="Appointment Date and Time"
                        name="appointmentTime"
                        type="datetime-local"
                        fullWidth
                        required
                        InputLabelProps={{ shrink: true }}
                        value={formData.appointmentTime}
                        onChange={handleChange}
                        sx={{ mb: 3 }} // Added bottom margin for spacing
                    />
                    
                    <TextField
                        label="Reason for Appointment"
                        name="reason"
                        multiline
                        rows={5} 
                        fullWidth
                        value={formData.reason}
                        onChange={handleChange}
                    />
                </Grid>

                {/* Submit Button (unchanged) */}
                <Grid item xs={12}>
                    <Button 
                        type="submit" 
                        variant="contained" 
                        size="large"
                        color="primary" 
                        fullWidth 
                        disabled={submitting}
                    >
                        {submitting ? <CircularProgress size={24} color="inherit" /> : 'Confirm Booking'}
                    </Button>
                </Grid>
            </Grid>
        </Box>
    </Layout>
);
};

export default BookAppointment;
