// src/components/Header.js
import React from 'react';
import { AppBar, Toolbar, Typography, Button, Box } from '@mui/material';
import LocalHospitalIcon from '@mui/icons-material/LocalHospital';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import './Header.css'; // Link the CSS file

const Header = () => {
    const { user, role, logout } = useAuth();
    const navigate = useNavigate();

    const handleLogout = () => {
        logout();
        navigate('/');
    };

    const getDashboardPath = (userRole) => {
        if (!userRole) return '/';
        // Extracts "admin", "doctor", or "patient" from ROLE_...
        return `/${userRole.split('_')[1].toLowerCase()}/dashboard`;
    };

    return (
        <AppBar position="static" className="hms-header">
            <Toolbar>
                
                {/* Brand/Logo Link */}
                <Typography variant="h6" component={Link} 
                    to={getDashboardPath(role)} 
                    className="hms-brand" 
                    sx={{ flexGrow: 1 }}> 
                    <LocalHospitalIcon sx={{ mr: 1 }} />
                    HMS | {role ? role.split('_')[1] : 'Guest'}
                </Typography>
                
                {/* User Info and Logout */}
                <Box sx={{ display: 'flex', alignItems: 'center' }}>
                    {user ? (
                        <>
                            <Typography variant="body1" component="span" className="hms-user-info">
                                Welcome, {user.username} ({role.split('_')[1]})
                            </Typography>
                            <Button color="inherit" onClick={handleLogout} variant="outlined">
                                Logout
                            </Button>
                        </>
                    ) : (
                        <Button color="inherit" component={Link} to="/">
                            Login
                        </Button>
                    )}
                </Box>
            </Toolbar>
        </AppBar>
    );
};

export default Header;
